import { useEffect, useRef, useState } from 'react' // ✅ Alphabetical Order

// ✅ External Library Imports First
import Loader from 'react-loaders'
import { Link } from 'react-router-dom'

// ✅ Internal Imports After External
import AnimatedLetters from '../AnimatedLetters'
import Logo from './Logo'
import LogoTitle from '../../assets/images/logo-s.png'

// ✅ Styles at the End
import './index.scss'



const Home = () => {
  const [letterClass, setLetterClass] = useState('text-animate')

  const nameArray = 'li Nafees'.split('')
  const jobArray = 'Cloud Architect & Security Engineer'.split('')

  // ✅ Refs for Sections
  const aboutRef = useRef(null)
  const skillsRef = useRef(null)
  const contactRef = useRef(null)

  useEffect(() => {
    const timer = setTimeout(() => {
      setLetterClass('text-animate-hover')
    }, 2500)
    return () => clearTimeout(timer)
  }, [])

  // ✅ Scroll Function
  const scrollToSection = (ref) => {
    ref.current?.scrollIntoView({ behavior: 'smooth' })
  }

  return (
    <>
      <div className="container home-page">
        <div className="text-zone">
          <h1>
            <span className={letterClass}>H</span>
            <span className={`${letterClass} _12`}>i There,</span>
            <br />
            <span className={`${letterClass} _13`}>I</span>
            <span className={`${letterClass} _14`}>'m</span>
            <img src={LogoTitle} alt="Muhammad Ali Nafees" />
            <AnimatedLetters letterClass={letterClass} strArray={nameArray} idx={15} />
            <br />
            <AnimatedLetters letterClass={letterClass} strArray={jobArray} idx={22} />
          </h1>
          <h2>Streamlining DevOps Workflows | Designing and Deploying Scalable & Secure Systems</h2>

          {/* ✅ Buttons Container with Scroll Links */}
          <div className="buttons-container">
            <button className="flat-button" onClick={() => scrollToSection(aboutRef)}>
              ABOUT ME
            </button>
            <button className="flat-button" onClick={() => scrollToSection(skillsRef)}>
              SKILLS
            </button>
            <button className="flat-button" onClick={() => scrollToSection(contactRef)}>
              CONTACT ME
            </button>
            <a href="/Resume.pdf" download className="flat-button download-button">
              DOWNLOAD RESUME
            </a>
          </div>
        </div>
        <Logo />
      </div>

      {/* ✅ Sections to Scroll To */}
      <section ref={aboutRef} id="about">
        <h2>About Me</h2>
        <p>Your about section content...</p>
      </section>

      <section ref={skillsRef} id="skills">
        <h2>Skills</h2>
        <p>Your skills section content...</p>
      </section>

      <section ref={contactRef} id="contact">
        <h2>Contact</h2>
        <p>Your contact section content...</p>
      </section>

      <Loader type="pacman" />
    </>
  )
}

export default Home
