import { useEffect, useState } from 'react'

// âœ… External Libraries (Sorted Alphabetically)
import Loader from 'react-loaders' // ðŸ”¼ Moved before react-router-dom
import { Link } from 'react-router-dom'
import styled, { css, keyframes } from 'styled-components'

// âœ… Internal Components (Sorted Alphabetically)
import AnimatedLetters from '../AnimatedLetters'
import Logo from './Logo'
import LogoTitle from '../../assets/images/logo-s.png'

// âœ… Styles at the End
import './index.scss'


// ðŸ”¥ Expand Animation (Button grows into full screen)
const expand = keyframes`
  0% {
    transform: scale(1);
  }
  100% {
    transform: scale(100);
    opacity: 0;
  }
`

const ExpandingButton = styled.a`
  display: inline-block;
  padding: 10px 20px;
  background-color: red;
  color: white;
  font-weight: bold;
  border: none;
  cursor: pointer;
  position: relative;
  transition: transform 0.3s ease;
  ${({ isExpanding }) =>
    isExpanding &&
    css`
      position: fixed;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      width: 200px;
      height: 50px;
      font-size: 1rem;
      text-align: center;
      z-index: 100;
      animation: ${expand} 5s forwards;
    `}
`

const Home = () => {
  const [letterClass, setLetterClass] = useState('text-animate')
  const [isExpanding, setIsExpanding] = useState(false)

  const nameArray = 'li Nafees'.split('')
  const jobArray = 'Cloud Architect & Security Engineer'.split('')

  useEffect(() => {
    const timer = setTimeout(() => {
      setLetterClass('text-animate-hover')
    }, 2500)

    // Start expansion after a delay
    const expandTimer = setTimeout(() => {
      setIsExpanding(true)
    }, 3000)

    // Trigger download after full expansion
    const downloadTimer = setTimeout(() => {
      window.location.href = '/Resume.pdf'
    }, 8000) // Matches animation duration

    return () => {
      clearTimeout(timer)
      clearTimeout(expandTimer)
      clearTimeout(downloadTimer)
    }
  }, [])

  return (
    <>
      <div className="container home-page">
        <div className="text-zone">
          <h1>
            <span className={letterClass}>H</span>
            <span className={`${letterClass} _12`}>i There,</span>
            <br />
            <span className={`${letterClass} _13`}>I</span>
            <span className={`${letterClass} _14`}>'m</span>
            <img src={LogoTitle} alt="Muhammad Ali Nafees" />
            <AnimatedLetters letterClass={letterClass} strArray={nameArray} idx={15} />
            <br />
            <AnimatedLetters letterClass={letterClass} strArray={jobArray} idx={22} />
          </h1>
          <h2>Streamlining DevOps Workflows | Designing and Deploying Scalable & Secure Systems</h2>

          {/* âœ… Buttons Container with Padding */}
          <div className="buttons-container">
            <Link to="/contact" className="flat-button">
              CONTACT ME
            </Link>
            <ExpandingButton
              href="#"
              isExpanding={isExpanding}
            >
              DOWNLOAD RESUME
            </ExpandingButton>
          </div>
        </div>
        <Logo />
      </div>

      <Loader type="pacman" />
    </>
  )
}

export default Home
