// ✅ React Imports First
import { useEffect, useRef, useState } from 'react'

// ✅ External Library Imports (Alphabetically Sorted)
import { throttle } from 'lodash' // <-- Move lodash to the top
import Loader from 'react-loaders'
import { useNavigate } from 'react-router-dom'

// ✅ Internal Component Imports
import AnimatedLetters from '../AnimatedLetters'
import Logo from './Logo'
import LogoTitle from '../../assets/images/logo-s.png'

// ✅ Styles at the End
import './index.scss'



const Home = () => {
  const [letterClass, setLetterClass] = useState('text-animate')
  const navigate = useNavigate() // ✅ React Router Navigation

  // ✅ Refs for Sections
  const aboutRef = useRef(null)
  const skillsRef = useRef(null)
  const contactRef = useRef(null)

  useEffect(() => {
    const timer = setTimeout(() => {
      setLetterClass('text-animate-hover')
    }, 2500)
    return () => clearTimeout(timer)
  }, [])

  // ✅ Scroll Handler (Throttle to Improve Performance)
  useEffect(() => {
    const handleScroll = throttle(() => {
      const scrollY = window.scrollY
      const aboutOffset = aboutRef.current?.offsetTop || 0
      const skillsOffset = skillsRef.current?.offsetTop || 0
      const contactOffset = contactRef.current?.offsetTop || 0

      // ✅ Change URL based on Scroll Position
      if (scrollY >= contactOffset - 100) {
        navigate('/contact', { replace: true })
      } else if (scrollY >= skillsOffset - 100) {
        navigate('/skills', { replace: true })
      } else if (scrollY >= aboutOffset - 100) {
        navigate('/about', { replace: true })
      } else {
        navigate('/', { replace: true })
      }
    }, 300) // ✅ Throttle to run every 300ms

    window.addEventListener('scroll', handleScroll)
    return () => window.removeEventListener('scroll', handleScroll)
  }, [navigate])

  return (
    <>
      <div className="container home-page">
        <div className="text-zone">
          <h1>
            <span className={letterClass}>H</span>
            <span className={`${letterClass} _12`}>i There,</span>
            <br />
            <span className={`${letterClass} _13`}>I</span>
            <span className={`${letterClass} _14`}>'m</span>
            <img src={LogoTitle} alt="Muhammad Ali Nafees" />
            <AnimatedLetters letterClass={letterClass} strArray={'li Nafees'.split('')} idx={15} />
            <br />
            <AnimatedLetters letterClass={letterClass} strArray={'Cloud Architect & Security Engineer'.split('')} idx={22} />
          </h1>
          <h2>Streamlining DevOps Workflows | Designing and Deploying Scalable & Secure Systems</h2>
        </div>
        <Logo />
      </div>

      {/* ✅ Sections to Scroll To */}
      <section ref={aboutRef} id="about">
        <h2>About Me</h2>
        <p>Your about section content...</p>
      </section>

      <section ref={skillsRef} id="skills">
        <h2>Skills</h2>
        <p>Your skills section content...</p>
      </section>

      <section ref={contactRef} id="contact">
        <h2>Contact</h2>
        <p>Your contact section content...</p>
      </section>

      <Loader type="pacman" />
    </>
  )
}

export default Home
