import { useEffect, useState } from 'react'

// âœ… External Library Imports First
import Loader from 'react-loaders'
import { Link } from 'react-router-dom'
import styled, { keyframes } from 'styled-components'

// âœ… Internal Imports After External
import Logo from './Logo'
import LogoTitle from '../../assets/images/logo-s.png'
import AnimatedLetters from '../AnimatedLetters'

// âœ… Styles at the End
import './index.scss'

// ðŸ”¥ Button Expand Animation
const expand = keyframes`
  0% {
    transform: scale(1);
  }
  100% {
    transform: scale(100);
    opacity: 0;
  }
`

const ExpandingButton = styled.a`
  display: inline-block;
  padding: 10px 20px;
  background-color: red;
  color: white;
  font-weight: bold;
  border: none;
  cursor: pointer;
  position: relative;
  transition: transform 0.3s ease;

  ${({ isExpanding }) =>
    isExpanding &&
    `
    animation: ${expand} 2s forwards;
    pointer-events: none;
  `}
`

const Home = () => {
  const [letterClass, setLetterClass] = useState('text-animate')
  const [isExpanding, setIsExpanding] = useState(false)

  const nameArray = 'li Nafees'.split('')
  const jobArray = 'Cloud Architect & Security Engineer'.split('')

  useEffect(() => {
    const timer = setTimeout(() => {
      setLetterClass('text-animate-hover')
    }, 2500)
    return () => clearTimeout(timer)
  }, [])

  const handleDownloadClick = () => {
    setIsExpanding(true)

    // Delay download until animation finishes
    setTimeout(() => {
      setIsExpanding(false) // Reset for future clicks
      window.location.href = '/Resume.pdf' // ðŸ”¥ Triggers the resume download
    }, 2000) // Matches animation duration
  }

  return (
    <>
      <div className="container home-page">
        <div className="text-zone">
          <h1>
            <span className={letterClass}>H</span>
            <span className={`${letterClass} _12`}>i There,</span>
            <br />
            <span className={`${letterClass} _13`}>I</span>
            <span className={`${letterClass} _14`}>'m</span>
            <img src={LogoTitle} alt="Muhammad Ali Nafees" />
            <AnimatedLetters letterClass={letterClass} strArray={nameArray} idx={15} />
            <br />
            <AnimatedLetters letterClass={letterClass} strArray={jobArray} idx={22} />
          </h1>
          <h2>Streamlining DevOps Workflows | Designing and Deploying Scalable & Secure Systems</h2>

          {/* âœ… Buttons Container with Padding */}
          <div className="buttons-container">
            <Link to="/contact" className="flat-button">
              CONTACT ME
            </Link>
            <ExpandingButton
              href="#"
              className="flat-button download-button"
              onClick={handleDownloadClick}
              isExpanding={isExpanding}
            >
              DOWNLOAD RESUME
            </ExpandingButton>
          </div>
        </div>
        <Logo />
      </div>

      <Loader type="pacman" />
    </>
  )
}

export default Home
